import { createStore } from "vuex";

export default createStore({
  state: {
    pageSize: 10,
    pageIndex: 1,
    category: "전체",
    itemsLength: 0,
  },
  mutations: {
    SET_PAGE_SIZE(state, value) {
      state.pageSize = value;
    },
    SET_PAEG_INDEX(state, value) {
      state.pageIndex = value;
    },
    SET_CATEGORY(state, value) {
      state.category = value;
    },
    SET_ITEMS_LENGTH(state, value) {
      state.itemsLength = value;
    },
  },
  actions: {
    setPageSize({ commit }, value) {
      commit("SET_PAGE_SIZE", value);
    },
    setPageIndex({ commit }, value) {
      commit("SET_PAGE_SIZE", value);
    },
    setCategory({ commit }, value) {
      commit("SET_CATEGORY", value);
    },
    setItemsLength({ commit }, value) {
      commit("SET_ITEMS_LENGTH", value);
    },
  },
});
