import { createStore } from "vuex";

export default createStore({
  state: {
    dmSearch: {
      pageSize: 10,
      pageIndex: 1,
      category: "전체",
      itemsLength: 0,
    },
  },
  mutations: {
    SET_DM_SEARCH(state, pageSize, pageIndex, category, itemsLength) {
      state.dmSearch.pageSize = pageSize;
      state.dmSearch.pageIndex = pageIndex;
      state.dmSearch.category = category;
      state.dmSearch.itemsLength = itemsLength;
    },
  },
  actions: {
    setDmSearch({ commit }, pageSize, pageIndex, category, itemsLength) {
      commit("SET_DM_SEARCH", pageSize, pageIndex, category, itemsLength);
    },
  },
});
